import { CurrencyIcon } from '@ya.praktikum/react-developer-burger-ui-components';
import ingredienCard from './IngredientCard.module.css';

const IngredientCard = ({item, setIngredients, ingredients, setOpen}) => {

    const [open, isOpen] = React.useState(false)

    return (
        <motion.div className={ingredienCard.card} onClick={() => setOpen(true)}>
            <motion.img src={item.image} alt='x'/>
            <motion.div className={ingredienCard.price_container}>
                <motion.p className="text text_type_digits-default">{item.price}</motion.p>
                <CurrencyIcon type="primary" />
            </motion.div>
            <motion.p className="text text_type_main-small">{item.name}</motion.p>
        </motion.div>
    )
}

export default IngredientCard;
