import React from 'react'
import burgerIngredients from '../BurgerIngredients/BurgerIngredients.module.css';
import { motion, AnimatePresence } from "framer-motion"
import IngredientCard from '../IngredientCard/IngredientCard';

function Category({setIngredients, ingredients, title, array}) {

  const [open, setOpen] = React.useState(false)

  return (
      <AnimatePresence>
        <div >
            <p className="text text_type_main-medium mt-10 mb-6">{title}</p>
            <div className={burgerIngredients.ingredients_container}>
                {array.map((item) => {return <motion.IngredientCard key={item._id} item={item} setIngredients={setIngredients} ingredients={ingredients}/>})}
            </div>
        </div>
      </AnimatePresence>
  )
}

export default Category
