import React from "react";
import IngredientCard from "../IngredientCard/IngredientCard";
import burgerIngredients from "./BurgerIngredients.module.css"
import { data } from "../../utils/data.js";



const BurgerIngredients = () => {
    const buns = data.filter((item) => item.type === 'bun')
    const sauces = data.filter((item) => item.type === 'sauce')
    const mains = data.filter((item) => item.type === 'main')

    const Tab = () => {
        return (
          <div style={{ display: 'flex' }}>
            <Tab value="one" active={current === 'one'} onClick={setCurrent}>
              One
            </Tab>
            <Tab value="two" active={current === 'two'} onClick={setCurrent}>
              Two
            </Tab>
            <Tab value="three" active={current === 'three'} onClick={setCurrent}>
              Three
            </Tab>
          </div>
        )
      }

    return (
        <div className={burgerIngredients.container}>
            <p className="text text_type_main-large">Соберите бургер</p>
            <Tab />
            <p className="text text_type_main-medium">Булки</p>
            <div className={burgerIngredients.ingredients_container}>
                {buns.map((objBuns) => {return <IngredientCard key={objBuns._id} name={objBuns.name} image={objBuns.image} price={objBuns.price}/>})}
            </div>
            <p className="text text_type_main-medium">Соусы</p>
            <div className={burgerIngredients.ingredients_container}>
                {sauces.map((objSauce) => {return <IngredientCard key={objSauce._id} name={objSauce.name} image={objSauce.image} price={objSauce.price}/>})}
            </div>
            <p className="text text_type_main-medium">Начинки</p>
            <div className={burgerIngredients.ingredients_container}>
                {mains.map((objMains) => {return <IngredientCard key={objMains._id} name={objMains.name} image={objMains.image} price={objMains.price}/>})}
            </div>

        </div>
    )
}

export default BurgerIngredients;
