import {ConstructorElement, DragIcon, Button } from '@ya.praktikum/react-developer-burger-ui-components';
import PriceIcon from '../UI/PriceIcon/PriceIcon';
import burgerConstructor from './BurgerConstructor.module.css';

const BurgerConstructor = ({ingredients, buns}) => {
    return (
        <div className={burgerConstructor.container}>
            <div className={burgerConstructor.main}>
                <ConstructorElement
                    type="top"
                    isLocked={true}
                    text={buns.name + ' (верх)'}
                    price={buns.price}
                    thumbnail={buns.image}

                />
                {ingredients.map((item) => <div>
                    <DragIcon type="primary" />
                    <ConstructorElement
                    text={item.name}
                    price={item.price}
                    thumbnail={item.image}
                />
                </div>)}
                {/* <ConstructorElement
                    text="Краторная булка N-200i (верх)"
                    price={50}
                /> */}
                <ConstructorElement
                    type="bottom"
                    isLocked={true}
                    text={buns.name + ' (низ)'}
                    price={buns.price}
                    thumbnail={buns.image}
                />
            </div>
            <div className={burgerConstructor.price}>
                <p className="text text_type_digits-medium">610</p>
                <PriceIcon />
                <Button htmlType="button" type="primary" size="large">Оформить Заказ</Button>
            </div>
        </div>

    )
}

export default BurgerConstructor;
