import React from "react";
import IngredientCard from "../IngredientCard/IngredientCard";
import { data } from "../../utils/data.js";



const BurgerIngredients = (props) => {

    const buns = data.filter((item) => item.type === 'bun').map((bunsObj) => {return [{bunsObj}]});
    const sauce = data.filter((item) => item.type === 'sauce').map((sauceObj) => {return [{sauceObj}]});
    const mains = data.filter((item) => item.type === 'main').map((mainsObj) => {return [{mainsObj}[0]]});

    return (
        <div className="ingredients-container">
            <p className="text text_type_main-large">Соберите бургер</p>
            <p className="text text_type_main-medium">Булки</p>
            <IngredientCard key={props.buns._id} name={props.buns.name} image={props.buns.image} price={props.buns.price}/>
            <p className="text text_type_main-medium">Соусы</p>
            <IngredientCard key={props.sauce._id} name={props.sauce.name} image={props.sauce.image} price={props.sauce.price}/>
            <p className="text text_type_main-medium">Начинки</p>
            <IngredientCard key={props.mains._id} name={props.mains.name} image={mains.image} price={mains.price}/>
        </div>
    )
}

export default BurgerIngredients;
