import React from "react";
import IngredientCard from "../IngredientCard/IngredientCard";
import { data } from "../../utils/data.js";



const BurgerIngredients = () => {
    const buns = data.filter((item) => item.type === 'buns')
    const sauce = data.filter((item) => item.type === 'sauce')
    const mains = data.filter((item) => item.type === 'mains')

    return (
        <div className="ingredients-container">
            <p className="text text_type_main-large">Соберите бургер</p>
            <p className="text text_type_main-medium">Булки</p>
            {sauce.map((objSauce) => {return <IngredientCard key={objSauce._id} name={objSauce.name} image={objSauce.image} price={objSauce.price}/>})}
            <p className="text text_type_main-medium">Соусы</p>
            {sauce.map((objSauce) => {return <IngredientCard key={objSauce._id} name={objSauce.name} image={objSauce.image} price={objSauce.price}/>})}
            <p className="text text_type_main-medium">Начинки</p>
            {mains.map((objMains) => {return <IngredientCard key={objMains._id} name={objMains.name} image={objMains.image} price={objMains.price}/>})}
        </div>
    )
}

export default BurgerIngredients;
