import React from "react";
import IngredientCard from "../IngredientCard/IngredientCard";
import { data } from "../../utils/data.js";

const BurgerIngredients = () => {

    const buns = { bunsImage, bunsName, bunsPrice }
    console.log(buns)
    const sauce = { sauceImage, sauceName, saucePrice }
    console.log(sauce)
    const mains = { mainsImage, mainsName, mainsPrice }
    console.log(mains)

    const bunsImage = data.filter((item) => item.type === 'bun').map(item=><span>{item.image}</span>);
    const bunsName = data.filter((item) => item.type === 'bun').map(item=><span>{item.name}</span>);
    const bunsPrice = data.filter((item) => item.type === 'bun').map(item=><span>{item.price}</span>);

    const sauceImage = data.filter((item) => item.type === 'sauce').map(item=><span>{item.image}</span>);
    const sauceName = data.filter((item) => item.type === 'sauce').map(item=><span>{item.name}</span>);
    const saucePrice = data.filter((item) => item.type === 'sauce').map(item=><span>{item.price}</span>);



    const mainsImage = data.filter((item) => item.type === 'main').map(item=><span>{item.image}</span>);;
    const mainsName = data.filter((item) => item.type === 'main').map(item=><span>{item.name}</span>);
    const mainsPrice = data.filter((item) => item.type === 'main').map(item=><span>{item.price}</span>);

    return (
        <>
            <p className="text text_type_main-medium">Соберите бургер</p>

        </>
    )
}

export default BurgerIngredients;
